import Runway from "@runwayml/sdk";

const runway = new Runway({ apiKey: process.env.RUNWAY_API_KEY });

const generation = await runway.imageToVideo.create({
  model: "gen3a_turbo",
  promptImage: imageUrl,
  promptText: "Camera slowly zooms in...",
  duration: 5,
  ratio: "16:9"
});

// Poll for completion
